import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import styled from "styled-components";

const ArticleTitles = styled.h2`
    font-size: 1.5rem;
    line-height: 1.25;
`;

const ArticleCards = styled.div`

    background-color:#1d5994;
    width: auto;
    padding: 1%;
    margin: 3% 5%;
    text-align: left;
    color: white;
    border-radius: 5px;
`;

const ArticlePTags = styled.p`
    margin: 2%;
    line-height: 1.25;
`;


const ApiTest = () => {

  const [articlesData, setArticlesData] = useState([])

  useEffect( () => {
    axios.get("https://api.nytimes.com/svc/topstories/v2/science.json?api-key=3ANiRLtIDcOCGFU0iqpojDGU1Sa8eDO7") 
      .then(response => {
        console.log('response', response.data.results)
        setArticlesData(response.data.results)
      })
      .catch(err => {
        console.log(err)
      })
  }, []);

  return (
    <div>
      <div>
        <h1>Some articles that may interest you...</h1>
      </div>
      <ArticleAbstract
        articlesData = {articlesData}
      />
    </div>
  );
};


const ArticleAbstract = props => {

    return (
        <div>
            {props.articlesData.map( (article) => {
                return (
                    <ArticleCards>
                        <ArticleTitles>{article.title}</ArticleTitles>
                        <ArticlePTags>date: {article.published_date}</ArticlePTags>
                        <ArticlePTags>Abstract: {article.abstract}</ArticlePTags>
                        <ArticlePTags>URL: {article.url}</ArticlePTags>
                    </ArticleCards>
                ) 
            })}
        </div>
    );
};

export default ApiTest;
