/*
import React from "react";
// See ReactWars for how to display info from an API.
const DisplayArticles = () => {
    return (
        <div>
            <h1>Articles that may interest you...</h1>
        </div>
    )
};

export default DisplayArticles;
*/

import React from 'react';
import './App.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import styled from "styled-components";


const App = () => {
  // Try to think through what state you'll need for this app before starting. Then build out
  // the state properties here.

  const [swapiData, setSwapiData] = useState([])

  // Fetch characters from the API in an effect hook. Remember, anytime you have a 
  // side effect in a component, you want to think about which state and/or props it should
  // sync up with, if any.

  useEffect( () => {
    axios.get("https://swapi.py4e.com/api/people/") 
      .then(response => {
        console.log('response', response.data.results)
        setSwapiData(response.data.results)
      })
      .catch(err => {
        console.log(err)
      })
  }, []);

  return (
    <div>
      <div className="App">
        <h1 className="Header">Characters</h1>
      </div>

      <Character
        swapiData = {swapiData}
      />
    </div>
  );
}

const StyledHeaders = styled.h2`
    fontsize: 1rem;
`;

const StyledCardBorderDiv = styled.div`
    border: 3px solid green;
    background-color: black; opacity: 0.6;
    width: 25%;
    margin 1% auto;
    text-align: center;
    color: yellow;
    border-radius: 10px;
`;

const Character = props => {

    return (
        <div>
            {props.swapiData.map( (charArr) => {
                return (
                    <StyledCardBorderDiv>
                        <StyledHeaders>{charArr.name}</StyledHeaders>
                        <p>Birth Year: {charArr.birth_year}</p>
                        <p>Height: {charArr.height}, Mass: {charArr.mass}, Gender: {charArr.gender}</p>
                        <p>Eye Color: {charArr.eye_color}, Hair Color: {charArr.hair_color}</p>
                    </StyledCardBorderDiv>
                ) 
            })}
        </div>
    );
};

export default Character;
