import React from "react";
import Loader from "react-loader-spinner";
import { connect } from "react-redux";
import styled from "styled-components";
import { Pin } from "@styled-icons/boxicons-solid/Pin";
import { addStudy } from "../actions";

const PinRed = styled(Pin)`
  color: red;
  height: 30px;
  width: 30px;
  transform: rotate(-20deg);
`;

class AddStudyForm extends React.Component {
  state = {
    study: {
      id: "",
      url: "",
      user_id: "",
      category_id: ""
    }
  };

  changeHandler = e => {
    this.setState({
      study: {
        ...this.state.study,
        [e.target.name]: e.target.value,
        user_id: this.props.id
      }
    });
  };

  addStudy = e => {
    e.preventDefault();
    this.props.addStudy(this.state.study);
    this.props.history.push("/home");
    this.setState({
      study: {
        id: "",
        url: "",
        user_id: "",
        category_id: ""
      }
    });
  };

  render() {
    return (
      <>
        <div className="main">
          <div className="add-study-form">
            <form onSubmit={this.addStudy}>
              <h2>
                <PinRed /> Add Pins to a Board
              </h2>

              <input
                type="text"
                name="title"
                placeholder="Title"
                onChange={this.changeHandler}
                value={this.state.study.title}
                required
              />
              <input
                type="url"
                name="url"
                placeholder="URL"
                onChange={this.changeHandler}
                value={this.state.study.url}
                required
              />
              <input
                type="number"
                name="category_id"
                placeholder="Board Id"
                onChange={this.changeHandler}
                value={this.state.study.category_id}
                required
              />

              <button>
                {this.props.addingStudy ? (
                  <Loader
                    type="TailSpin"
                    color="white"
                    height={18}
                    width={18}
                  />
                ) : (
                  "Add Pin"
                )}
              </button>
            </form>
          </div>
        </div>
      </>
    );
  }
}

const mapStateToProps = ({ addingStudy, id }) => ({
  addingStudy,
  id
});

export default connect(
  mapStateToProps,
  { addStudy }
)(AddStudyForm);
