import React, { useState } from "react";
import styled from "styled-components";
import * as yup from "yup";

const FormHeader = styled.h1`
    text-align: center;
`;

const Subheadings = styled.h2`
    font-size: 2rem;
    padding: 5% 0;
`;

const AlignedDropdownDiv = styled.div`
    text-align: center;
    padding: 5%;
`;

const AddArticleButton = styled.button`
    width: auto;

`;

const formSchema = yup.object().shape({
    articleUrl: yup 
        .string()
        .required("An article URL is required."),
    category: yup
        .string()
        .required("An article category is required.")
});

const AddArticle = (props) => {

    const [formState, setFormState] = useState({
        articleUrl: "",
        category: ""
    });

    const [errorState, setErrorState] = useState({
        articleUrl: "",
        category: ""
    })

    const clearForm = () => {
        setFormState({ 
            articleUrl: "",
            category: ""
        });
    };

    const validate = e => {
        let value = e.target.value
        yup 
            .reach(formSchema, e.target.name)
            .validate(value)
            .then(valid => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: ""
                });
            })
            .catch(err => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: err.errors[0]
                });
            });
    };

    const inputChange = e => {
        e.persist();
        validate(e);
        let value = e.target.value
        setFormState({
            ...formState, 
            [e.target.name]: value
        });
    };

    const formSubmit = e => {
        e.preventDefault();
        console.log("form submitted");
        props.handleFormSubmission(formState);
        clearForm();
    };

    return (
        <div>
            <FormHeader>Add your articles here.</FormHeader>
            <form onSubmit={formSubmit}>
                <label htmlFor="articleUrl">
                <Subheadings>Article URL</Subheadings>
                <input
                    type="text"
                    name="articleUrl"
                    id="articleUrl"
                    value={formState.articleUrl}
                    onChange={inputChange}
                />
                {errorState.length > 0 ? (
                    <p>{errorState.articleUrl}</p>
                ) : null}
                </label>

                <AlignedDropdownDiv>
                <label htmlFor="category">
                <Subheadings>Article Category:</Subheadings>
                <select
                    name="category"
                    id="category"
                    value={formState.category}
                    onChange={inputChange}
                >
                    <option value="Education">Education</option>
                    <option value="Recreation">Recreation</option>
                    <option value="Cooking">Cooking</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Technology">Technology</option>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Professional">Professional</option>
                    <option value="Health">Health</option>
                    <option value="Sports">Sports</option>
                    <option value="Other">Other</option>
                </select>
                </label>
                </AlignedDropdownDiv>

                <AddArticleButton>Add Article</AddArticleButton>
            </form>
        </div>
    );
};

export default AddArticle;
